INSERT INTO `book` VALUES ('shnn02303', 'Little Stories - The Best Book For Your Leisure Time', '<p>Tiếng Anh hiện nay đã là một ngôn ngữ phổ biến tại Việt Nam, được nhiều người sử dụng thường xuyên trong học tập, công việc hay sinh hoạt đời thường. Ngoài vai trò là một ngôn ngữ mang lại lợi thế to lớn trong cuộc sống, tiếng Anh còn được nhiều người tìm đến để mở mang vốn kiến thức, hay đơn giản là một thú vui, thú đọc sau một ngày căng thẳng và mệt mỏi.</p><p>Bộ sách Little Stories, với những câu chuyện hay, thú vị được sưu tầm trên khắp thế giới, sẽ là một trải nghiệm mới dành cho độc giả. Ngoài chia sẻ những câu chuyện nhẹ nhàng, những bài học ý nghĩa, bộ sách còn giúp người đọc tự trau dồi vốn tiếng Anh ngay trong lúc thư giãn. Chúng tôi hy vọng bộ sách này sẽ luôn đồng hành cùng quý độc giả, luôn đem lại cho các bạn những giây phút nhẹ nhàng và thư thái.</p>', '14 x 20 cm', 'images/books/shnn02303/0ebc85b8b69b60e16c9f17ac2f726697.jpg', 'hcm', 91, 16, 299, NULL, NULL, 50000, NULL, 0, '2021-08-02', 2020, 3, 8794069302169, 300, 'vi', 20, '2021-08-02 16:19:41', 'loc1@1', NULL, NULL, '2021-08-02 16:19:41', NULL);
